import React, { Component } from "react";

class Settings extends Component {

    constructor(props) {
        super(props);

        this.state = {
            player1: "",
            player2: "",
        };

        this.handleSubmit = this.handleSubmit.bind(this);
        this.handleChangeP1 = this.handleChangeP1.bind(this);
        this.handleChangeP2 = this.handleChangeP2.bind(this);
    }

    handleChangeP1(e) {
        this.setState({ player1: e.currentTarget.value });
    }

    handleChangeP2(e) {
        this.setState({ player2: e.currentTarget.value });
    }

    handleSubmit(e) {
        e.preventDefault();
    }

    render() {

        let { player1 } = this.state;
        let { player2 } = this.state;

        return (
            <form className="form-group" onSubmit={this.handleSubmit} >
                <label className="font-weight-bold" for="player1Name">Player 1 Name:</label>
                <input className="form-control"
                    onChange={this.handleChangeP1}
                    value={player1}>
                </input>

                <label className="font-weight-bold" for="player2Name">Player 2 Name:</label>
                <input
                    className="form-control"
                    onChange={this.handleChangeP2}
                    value={player2}>
                </input>
            </form>
        );
    };
}

export default Settings;
