import React, { Component } from "react";

class Settings extends Component {

    constructor(props) {
        super(props);

        this.state = {
            player1Name: props.player1Name,
            player2Name: props.player2Name,
            winningScore: props.winningScore,
            alernate: props.alernate,
        };

        this.handleSubmit = this.handleSubmit.bind(this);
        this.handleChangeP1 = this.handleChangeP1.bind(this);
        this.handleChangeP2 = this.handleChangeP2.bind(this);
        this.winningScore = this.winningScore.bind(this);
        // this.alernate = this.alernate.bind(this);
    }

    handleChangeP1(e) {
        this.setState({ player1Name: e.currentTarget.value });
    }

    handleChangeP2(e) {
        this.setState({ player2Name: e.currentTarget.value });
    }

    winningScore(e) {
        this.setState({ winningScore: e.currentTarget.value });
    }

    // alernate(e) {
    //     this.setState({})
    // }


    handleSubmit(e) {
        e.preventDefault();
        this.props.handleSubmit({ ...this.state });
    }

    render() {
        let { player1Name } = this.state;
        let { player2Name } = this.state;

        return (
            <form className="form-group" onSubmit={this.handleSubmit} >
                <div className="container">
                    <br />
                    {/* Player 1 Name */}
                    <label for="player1Name">Player 1 Name:</label>
                    <input
                        className="form-control"
                        onChange={this.handleChangeP1}
                        value={player1Name}
                        required>
                    </input>
                    <br />
                    {/* Player 2 Name */}
                    <label for="player2Name">Player 2 Name:</label>
                    <input
                        className="form-control"
                        onChange={this.handleChangeP2}
                        value={player2Name}
                        required>
                    </input>
                    <br />
                    <div class="form-group">
                        <label for="exampleFormControlSelect1">Example select</label>
                        <select class="form-control" id="exampleFormControlSelect1">
                            <option>1</option>
                            <option>2</option>
                            <option>3</option>
                            <option>4</option>
                            <option>5</option>
                        </select>
                    </div>

                    <hr />

                    {/* Submit Form */}
                    <button className="btn btn-primary">Start Game</button>
                </div>
            </form >
        );
    };
}

export default Settings;
