import { connect } from "react-redux";

import Player from "./Player";

const mapStateToProps = state => {
    return {
        playerName: "Player 2",
        winner: state.winner,
        score: state.store,
        p1Serving: !state.p1Serving,
    };
};

export default connect(mapStateToProps)(Player);
