import React from "react";
import Buttons from "./components/Button"
import Player from "./components"

const App = ({
  player1,
  player2,
  handleP1Increment,
  handleP2Increment,
  handleReset,
  p1Serving,
  winner }) => (
    <React.Fragment>

      <Buttons
        handleIncrement={handleP1Increment}
        handleDecrement={handleP2Increment}
        handleReset={handleReset}
      />


      {/* header */}
      <header className="jumbotron mt-4 mb-0">
        <h1>PongPing</h1>
      </header>

      {/* scores */}
      <div className="row mb-4">
        <div className="col-md-6 mt-4">
          <div className={"card text-center" + (p1Serving ? " bg-dark text-white" : "")}>
            <h5 className="card-header">Player 1</h5>
            <div className="card-body">
              <p className="card-text display-1">{player1}</p>
            </div>
            <div className="card-footer">
              <button className="form-control btn btn-success" onClick={handleP1Increment}>+</button>
            </div>
          </div>
        </div>

        <div className="col-md-6 mt-4">
          <div className={"card text-center" + (p1Serving ? "" : " bg-dark text-white")}>
            <h5 className="card-header">Player 2</h5>
            <div className="card-body">
              <p className="card-text display-1">{player2}</p>
            </div>
            <div className="card-footer">
              <button className="form-control btn btn-success" onClick={handleP2Increment}>+</button>
            </div>
          </div>
        </div>
      </div>

      <Players>
        <Player
          playerName={"Player1"}
          score={player1}
          handleClick={handleP1Increment}
          serving={p1Serving === 1}
          disabled={winner > 0 ? true : false} />

        <Player
          playerName={"Player2"}
          score={player2}
          handleClick={handleP2Increment}
          serving={p1Serving === 2}
          disabled={winner > 0 ? true : false} />
      </Players>


      {winner > 0 ? (
        <h2 className="alert alert-success"> Player {winner} wins!</h2>
      ) : null}
      <hr />

      { /* reset button */}
      <button className="btn btn-danger" onClick={handleReset}>Reset</button>
    </React.Fragment >
  );

export default App;
